import { create } from 'zustand';
import axios from 'axios';

const exercisesStore = create((set) => ({
    exercises: null,

    updating: false,

    emptyExercise: {
        number: "",
        exercise: "",
        weight: "",
        sets: "",
        reps: "",
        intensity: "",
        notes: "",
        workout: null,
        log: null
    },

    updateForm: {
        exercise: "",
        weight: "",
        sets: "",
        reps: "",
        intensity: "",
        notes: ""
    },

    setUpdating: () => {
        set({
            updating: true
        })
    },

    updateUpdateFormField: (e) => {
        const {name, value} = e.target;

        set(state => {
            return {
                updateForm: {
                    ...state.updateForm,
                    [name]: value
                }
            }
        });
    },

    fetchExercises: async (_id) => {
        const res = await axios.get(`/workouts/${_id}/exercises`);
        set({
            exercises: res.data.exercises
        });
    },

    createExercise: async (e, workoutId) => {
        //create an empty exercise that will be updated
        e.preventDefault();

        //get log id
        const workout = await axios.get(`/workouts/${workoutId}`);

        set((state) => {
            return {
                emptyExercise: {
                    ...state.emptyExercise,
                    workout: workoutId,
                    log: workout.data.log
                }
            };
        });

        const { emptyExercise, exercises } = exercisesStore.getState();

        const res = await axios.post("/exercises", emptyExercise);

        set({
            exercises: [...exercises, res.data.myExercise]
        });
        set((state) => {
            return {
                emptyExercise: {
                    ...state.emptyExercise,
                    workout: null,
                    log: null
                }
            };
        });
    },


    deleteExercise: async (_id) => {
        const { exercises } = exercisesStore.getState();

        await axios.delete(`/exercises/${_id}`);

        const newExercises = exercises.filter((exercise) => {
            return exercise._id !== _id;
        });

        set({
            exercises: newExercises
        });
    },

    /*

    toggleUpdate: (exercise) => {
        set({
            updateForm: {
                name: exercise.name,
                body: exercise.body,
                _id: exercise._id
            }
        });
    },

    toggleCreate: (workout) => {
        set({
            create: true
        });
    },
    */

    updateExercise: async (e) => {
        e.preventDefault();
        const {updateForm: {exercise, weight, sets, reps, intensity, notes}, exercises} = exercisesStore.getState();
        const res = await axios.put(`/workouts/${_id}`, {title, body, date});

        const newWorkouts = [...workouts];
        const workoutIndex = workouts.findIndex((workout) => {
            return workout._id === _id;
        });
        newWorkouts[workoutIndex] = res.data.workout;

        set({
            workouts: newWorkouts,
            updateForm: {
                title: "", body: "", _id: null, date: null
            }
        });
    }
  }));

export default exercisesStore;
