import { create } from 'zustand';
import axios from 'axios';

const logsStore = create((set) => ({
    logs: null,

    createForm: {
        title: "",
        body: ""
    },

    updateForm: {
        title: "",
        body: "",
        _id: null
    },

    updateCreateFormField: (e) => {
        const {name, value} = e.target;
        set((state) => {
            return {
                createForm: {
                    ...state.createForm,
                    [name]: value
                }
            };
        });
    },

    updateUpdateFormField: (e) => {
        const {name, value} = e.target;

        set(state => {
            return {
                updateForm: {
                    ...state.updateForm,
                    [name]: value
                }
            }
        });
    },

    fetchLogs: async () => {
        const res = await axios.get("/logs");
        set({
            logs: res.data.logs
        });
    },

    createLog: async (e) => {
        const { createForm, logs } = logsStore.getState();
        e.preventDefault();
        await axios.post("/logs", createForm);

        set({
            logs: [...logs, res.data.log]
        });

        set({
            createForm: { title: "", body: ""}
        });
    },

    deleteLog: async (_id) => {
        await axios.delete(`/logs/${_id}`);
    },

    toggleUpdate: (log) => {
        set({
            updateForm: {
                title: log.title,
                body: log.body,
                _id: log._id
            }
        });
    },

    updateLog: async (e) => {
        e.preventDefault();
        const {title, body, _id} = logsStore.getState().updateForm;
        await axios.put(`/logs/${_id}`, {title, body});
        set({
            updateForm: {
                title: "", body: "", _id: null
            }
        });
    }
  }));

export default logsStore;
