import { create } from 'zustand';
import axios from 'axios';

const exercisesStore = create((set) => ({
    exercises: null,
    create: false,
    rows: null,

    //may not need
    createForm: {
        number: "",
        exercise: "",
        weight: "",
        sets: "",
        reps: "",
        intensity: "",
        notes: "",
        _id: null
    },

    /*
    updateForm: {
        title: "",
        body: "",
        date: null,
        _id: null
    },
    */

    setCreateFalse: () => {
        set({
            create: false
        });
    },

    /*
    updateCreateFormField: (e) => {
        const {name, value} = e.target;
        set((state) => {
            return {
                createForm: {
                    ...state.createForm,
                    [name]: value
                }
            };
        });
    },
    */

    updateUpdateFormField: (e) => {
        const {name, value} = e.target;

        set(state => {
            return {
                updateForm: {
                    ...state.updateForm,
                    [name]: value
                }
            }
        });
    },

    fetchExercises: async (_id) => {
        const res = await axios.get(`/workouts/${_id}/exercises`);
        set({
            exercises: res.data.exercises
        });
    },

    createExercise: async (e) => {
        //create an empty exercise that will be updated
        const { createForm, exercises } = exercisesStore.getState();
        e.preventDefault();
        const res = await axios.post("/workouts", emptyExercise);

        set({
            exercises: [...exercises, res.data.exercise],
        });
    },

    deleteExercise: async (_id) => {
        const { exercises } = exercisesStore.getState();

        await axios.delete(`/exercises/${_id}`);

        const newExercises = exercises.filter((exercise) => {
            return exercise._id !== _id;
        });

        set({
            workouts: newWorkouts
        });
    },

    toggleUpdate: (exercise) => {
        set({
            updateForm: {
                name: exercise.name,
                body: exercise.body,
                _id: exercise._id
            }
        });
    },

    toggleCreate: (workout) => {
        set({
            create: true
        });
    },

    updateWorkout: async (e) => {
        e.preventDefault();
        const {updateForm: {title, body, date, _id}, workouts} = workoutsStore.getState();
        const res = await axios.put(`/workouts/${_id}`, {title, body, date});

        const newWorkouts = [...workouts];
        const workoutIndex = workouts.findIndex((workout) => {
            return workout._id === _id;
        });
        newWorkouts[workoutIndex] = res.data.workout;

        set({
            workouts: newWorkouts,
            updateForm: {
                title: "", body: "", _id: null, date: null
            }
        });
    }
  }));

export default exercisesStore;
