import React, { useState, useEffect } from "react";
import axios from "axios";
import Logs from "./Logs";
import * as Functions from "../stores/Functions"

function App() {
  //set component state
  const [logs, setLogs] = useState(null);
  const [createForm, setCreateForm] = useState({
    title: "",
    body: ""
  });
  const [updateForm, setUpdateForm] = useState({
    title: "",
    body: "",
    _id: null
  });

  //Functions


  useEffect(() => {
    //useEffect to fetch data after render
    fetchLogs();
  });

  return (
    <div className="App">
      <Logs />

      {updateForm._id && <div>
        <h2>Update Log</h2>
        <form onSubmit={updateLog}>
          <input name="title" value={updateForm.title} onChange={updateUpdateFormField}/>
          <textarea name="body" value={updateForm.body} onChange={updateUpdateFormField}/>
          <button type="submit">Update Log</button>
        </form>
      </div>}

      {!updateForm._id && <div>
        <h2>Create Log</h2>
        <form onSubmit={createLog}>
          <input name="title" value={createForm.title} onChange={updateCreateFormField}/>
          <textarea name="body" value={createForm.body} onChange={updateCreateFormField}/>
          <button type="submit">Create Log</button>
        </form>
      </div>}
    </div>
  );
}

export default App;
