import { create } from 'zustand';
import axios from 'axios';
import { Navigate, useNavigate } from 'react-router-dom';

const logsStore = create((set) => ({
    logs: null,

    create: false,

    createForm: {
        title: "",
        body: "",
        type: "",
        _id: null
    },

    updateForm: {
        title: "",
        body: "",
        _id: null
    },

    setCreateFalse: () => {
        set({
            create: false
        });
    },

    updateCreateFormField: (e) => {
        const {name, value} = e.target;
        set((state) => {
            return {
                createForm: {
                    ...state.createForm,
                    [name]: value
                }
            };
        });
    },

    updateUpdateFormField: (e) => {
        const {name, value} = e.target;

        set(state => {
            return {
                updateForm: {
                    ...state.updateForm,
                    [name]: value
                }
            }
        });
    },

    fetchLogs: async () => {
        const res = await axios.get("/logs");
        set({
            logs: res.data.logs
        });
    },

    createLog: async (e) => {
        const { createForm, logs } = logsStore.getState();
        e.preventDefault();
        const res = await axios.post("/logs", createForm);

        set({
            logs: [...logs, res.data.log],
            createForm: { title: "", body: "", type: ""},
            create: false
        });
    },

    deleteLog: async (_id) => {
        const { logs } = logsStore.getState();

        //Delete logbook
        await axios.delete(`/logs/${_id}`);

        const newLogs = logs.filter((log) => {
            return log._id !== _id;
        });

        set({
            logs: newLogs
        });
    },

    toggleUpdate: (log) => {
        set({
            updateForm: {
                title: log.title,
                body: log.body,
                _id: log._id
            }
        });
    },

    toggleCreate: (log) => {
        set({
            create: true
        });
    },

    updateLog: async (e) => {
        e.preventDefault();
        const {updateForm: {title, body, _id}, logs} = logsStore.getState();
        const res = await axios.put(`/logs/${_id}`, {title, body});

        const newLogs = [...logs];
        const logIndex = logs.findIndex((log) => {
            return log._id === _id;
        });
        newLogs[logIndex] = res.data.log;

        set({
            logs: newLogs,
            updateForm: {
                title: "", body: "", _id: null
            }
        });
    },

    viewWorkoutsOrMeals: (type) => {
        const navigate = useNavigate();
        if (type.localeCompare("Exercise"))
            navigate(`/logs/${log._id}/workouts`);
        else
            navigate(`/logs/${log._id}/meals`);
    }
  }));

export default logsStore;
