import React, { useEffect } from "react";
import logsStore from "../stores/logsStore";
import Logs from "./Logs";

function App() {
  const store = logsStore();

  useEffect(() => {
    //useEffect to fetch data after render
    store.fetchLogs();
  });

  return (
    <div className="App">
      <Logs />

      {store.updateForm._id && <div>
        <h2>Update Log</h2>
        <form onSubmit={store.updateLog}>
          <input name="title" value={store.updateForm.title} onChange={store.updateUpdateFormField}/>
          <textarea name="body" value={store.updateForm.body} onChange={store.updateUpdateFormField}/>
          <button type="submit">Update Log</button>
        </form>
      </div>}

      {!store.updateForm._id && <div>
        <h2>Create Log</h2>
        <form onSubmit={store.createLog}>
          <input name="title" value={store.createForm.title} onChange={store.updateCreateFormField}/>
          <textarea name="body" value={store.createForm.body} onChange={store.updateCreateFormField}/>
          <button type="submit">Create Log</button>
        </form>
      </div>}
    </div>
  );
}

export default App;
